import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { createProduct, getProducts } from "../ProductActions/ProductActions";
import { getCategories } from "../categoryActions/CategoryActions";
import { getIngredients } from "../IngredientActions/IngredientActions";
import classnames from "classnames";

class AddOrders extends Component {
  componentDidMount() {
    this.props.getCategories();
    this.props.getIngredients();
    //this.props.getProducts();
  }
  constructor() {
    super();

    this.state = {
      products: {},
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.onClick = this.onClick.bind(this);
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }
  onClick(e) {
    console.log(e.target.getAttribute("data-categoryId"));
    this.props.getProducts();
    console.log(this.props.products);
    this.setState({ products: this.props.products });
    //const { products } = this.props.products;
  }

  onSubmit(e) {
    e.preventDefault();
  }
  render() {
    const { categories } = this.props.categories;
    const { ingredients } = this.props.ingredients;
    const { products } = this.props.products;
    //const { products } = this.props.products;
    return (
      <div className="content-wrapper">
        <div>
          <div className="register">
            <div className="container">
              <div className="row">
                <div className="col-md-8 m-auto">
                  <p className="lead text-center">Add New Order</p>
                  <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                      <ul class="list-group">
                        {categories.map((category) => (
                          <li
                            className="list-group-item"
                            data-categoryId={category.id}
                            onClick={this.onClick}
                          >
                            {category.categoryName}
                          </li>
                        ))}
                      </ul>
                      <p>
                        {products.map((product) => (
                          <p>{product.name}</p>
                        ))}
                      </p>
                    </div>
                    <div className="form-group">
                      <ul class="list-group">
                        {ingredients.map((ingredient) => (
                          <li
                            className="list-group-item"
                            data-ingredientId={ingredient.id}
                          >
                            {ingredient.name}
                          </li>
                        ))}
                      </ul>
                      <p>Show list of products</p>
                    </div>

                    <input
                      type="submit"
                      className="btn btn-info btn-block mt-4"
                    />
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  //errors: state.errors,
  categories: state.category,
  ingredients: state.ingredient,
  products: state.products,
});
export default connect(mapStateToProps, {
  getCategories,
  getIngredients,
  getProducts,
})(AddOrders);
