import React, { Component } from "react";
import { connect } from "react-redux";
import { getClients } from "../src/Actions/ClientActions";
import PropTypes from "prop-types";
import Clientitem from "./Actions/Clientitem";
import { Link } from "react-router-dom";
import "./datatables.css";

const $ = require("jquery");
$.DataTable = require("datatables.net");

const styleBackground = {
  backgroundColor: "#34495e",
};
const fields = ["name", "registered", "role", "status"];
class Tables extends Component {
  componentDidMount() {
    this.props.getClients();
    this.$el = $(this.el);
    this.$el.DataTable({
      data: this.props.data,
      columns: [
        { titre: "firstName" },
        { title: "lastName" },
        { title: "Age" },
      ],
    });
  }

  render() {
    const { clients } = this.props.client;
    return (
      <div className="content-wrapper">
        <div className="container">
          <div className="projects">
            <div className="container">
              <div className="row">
                <div className="col-md-12">
                  <h1 className="display-4 text-center">Clients</h1>
                  <br />
                  <Link
                    to="addclient"
                    className="btn btn-lg btn-info float-right mb-2"
                  >
                    Add Client
                  </Link>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
Tables.propTypes = {
  client: PropTypes.object.isRequired,
  getClients: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  client: state.client,
});

export default connect(mapStateToProps, { getClients })(Tables);
