import { GET_INGREDIENTS, GET_INGREDIENT } from "../IngredientActions/Types";

const initialState = {
  ingredients: [],
  ingredient: {},
};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_INGREDIENTS:
      return {
        ...state,
        ingredients: action.payload,
      };
    case GET_INGREDIENT:
      return {
        ...state,
        ingredient: action.payload,
      };

    default:
      return state;
  }
}
