import React, { Component } from "react";
import { connect } from "react-redux";
import { getTables } from "./TablesActions/TablesActions";
import PropTypes from "prop-types";
import { Link } from "react-router-dom";
import Tableitem from "./Tableitem";
import $ from "jquery";
import "bootstrap/dist/css/bootstrap.min.css";
import "jquery/dist/jquery.min.js";
//Datatable Modules
import "datatables.net-dt/js/dataTables.dataTables";
import "datatables.net-dt/css/jquery.dataTables.min.css";

class table extends Component {
  componentDidMount() {
    this.props.getTables();
    $(function () {
      $("#example1").DataTable();
    });
  }

  render() {
    const { table } = this.props.table;
    return (
      <div className="content-wrapper">
        <div className="container">
          <div className="projects">
            <div className="container">
              <div className="row">
                <div className="col-md-12">
                  <h1 className="display-4 text-center">Tables</h1>
                  <br />
                  <Link
                    to="addingredients"
                    className="btn btn-lg btn-info float-right mb-2"
                  >
                    Add Table
                  </Link>
                  <table
                    id="example1"
                    class="table table-bordered table-striped"
                  >
                    <thead>
                      <tr>
                        <th>Nombre de place</th>
                        <th>numero de table</th>
                      </tr>
                    </thead>
                    <tbody>
                      {Tables.map((table) => (
                        <Tableitem key={table.id} table={table} />
                      ))}
                    </tbody>
                  </table>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
Listetables.propTypes = {
  table: PropTypes.object.isRequired,
  getTables: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  table: state.table,
});

export default connect(mapStateToProps, { getTables })(table);
