import { GET_CATEGORIES } from "../categoryActions/Types";

const initialState = {
  categories: [],
  category: {},
};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_CATEGORIES:
      return {
        ...state,
        categories: action.payload,
      };
    case GET_CATEGORY:
    default:
      return state;
  }
}
