import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { createClient } from "./Actions/ClientActions";
class AddClient extends Component {
  constructor() {
    super();
    this.state = {
      firstName: "",
      lastName: "",
      adresse: "",
      year: "",
      email: "",
      phone: "",
      codeClient: "",
      errorsfirstName: "",
      errorslastName: "",
      errorsadresse: "",
      errorsyear: "",
      errorsemail: "",
      errorsphone: "",
      errorscodeClient: "",
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }
  componentWillReceiveProps(nextProps) {
    if (nextProps.errors) {
    }
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }
  validateForm() {
    return true;
  }
  onSubmit(e) {
    if (this.validateForm()) {
      e.preventDefault();
      const {
        firstName,
        lastName,
        adresse,
        year,
        email,
        phone,
        codeClient,
      } = this.state;
      if (firstName == "") {
        this.setState({
          errorsfirstName: " First Name field is Required !",
        });
      }
      const newClient = {
        firstName: this.state.firstName,
        lastName: this.state.lastName,
        adresse: this.state.adresse,
        year: this.state.year,
        email: this.state.email,
        phone: this.state.phone,
        codeClient: this.state.codeClient,
      };
      this.props.createClient(newClient, this.props.history);
    }
  }
  render() {
    return (
      <div className="content-wrapper">
        <div>
          <meta charSet="utf-8" />
          <meta
            name="viewport"
            content="width=device-width, initial-scale=1, shrink-to-fit=no"
          />

          <link
            rel="stylesheet"
            href="https://stackpath.bootstrapcdn.com/bootstrap/4.1.1/css/bootstrap.min.css"
            integrity="sha384-WskhaSGFgHYWDcbwN70/dfYBj47jz9qbsMId/iRN3ewGhXQFZCSftd1LZCfmhktB"
            crossOrigin="anonymous"
          />
          <link rel="stylesheet" href="App.css" />
          <link
            rel="stylesheet"
            href="https://use.fontawesome.com/releases/v5.2.0/css/all.css"
            integrity="sha384-hWVjflwFxL6sNzntih27bfxkr27PmbbK/iSvJ+a4+0owXq79v+lsFkW54bOGbiDQ"
            crossOrigin="anonymous"
          />

          <div className="register">
            <div className="container">
              <div className="row">
                <div className="col-md-8 m-auto">
                  <p className="lead text-center">Add New Client</p>
                  <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="First Name"
                        name="firstName"
                        value={this.state.firstName}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="Last Name"
                        name="lastName"
                        value={this.state.lastName}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>
                    <div className="form-group">
                      {this.state.formErrors.adresse}
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="Adresse "
                        name="adresse"
                        value={this.state.adresse}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="int"
                        className="form-control form-control-lg"
                        placeholder="Year"
                        name="year"
                        value={this.state.year}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>

                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="Email"
                        name="email"
                        value={this.state.email}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="Phone"
                        name="phone"
                        value={this.state.phone}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="Code Client"
                        name="codeClient"
                        value={this.state.codeClient}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>

                    <input
                      type="submit"
                      className="btn btn-info btn-block mt-4"
                    />
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
AddClient.propTypes = {
  createClient: PropTypes.func.isRequired,
};
const mapStateToProps = (state) => ({
  errors: state.errors,
});
export default connect(null, { createClient })(AddClient);
