import React, { Component } from "react";
import { connect } from "react-redux";
import { getClients } from "../src/Actions/ClientActions";
import PropTypes from "prop-types";
import {
  CBadge,
  CCard,
  CCardBody,
  CCardHeader,
  CCol,
  CDataTable,
  CRow,
} from "@coreui/react";
import { DocsLink } from "../src/reusable";

import usersData from "../src/users/UsersData";

const getBadge = (status) => {
  switch (status) {
    case "Active":
      return "success";
    case "Inactive":
      return "secondary";
    case "Pending":
      return "warning";
    case "Banned":
      return "danger";
    default:
      return "primary";
  }
};
const fields = ["name", "registered", "role", "status"];

class Tables extends Component {
  render() {
    return (
      <div className="content-wrapper">
        <br />
        <br />
        <br />

        <CRow>
          <CCol xs="18" lg="12">
            <CCard>
              <CCardHeader>
                Listes des clients
                <DocsLink name="CModal" />
              </CCardHeader>
              <CCardBody>
                <CDataTable
                  items={usersData}
                  fields={fields}
                  itemsPerPage={5}
                  pagination
                  scopedSlots={{
                    status: (item) => (
                      <td>
                        <CBadge color={getBadge(item.status)}>
                          {item.status}
                        </CBadge>
                      </td>
                    ),
                  }}
                />
              </CCardBody>
            </CCard>
          </CCol>
        </CRow>
      </div>
    );
  }
}

Tables.propTypes = {
  client: PropTypes.object.isRequired,
  getClients: PropTypes.func.isRequired,
};

const mapStateToProps = (state) => ({
  client: state.client,
});

export default connect(mapStateToProps, { getClients })(Tables);
