import React, { Component } from "react";

const OrderSummary = (props) => {
  console.log(props);

  /*<button onClick={this.printReceipt}>Print Receipt</button>
  /*printReceipt() {
    window.print();
  }
  */

  let totalAmount = (
    <div class="summary clearfix">
      <div class="line">
        <div class="entry total">
          <span class="badge">Total: </span>
          <span class="value">00.00 $</span>
        </div>
      </div>
    </div>
  );

  let billDetail = Object.keys(props.orders).map((order) => {
    let totalAmount = 0;
    if (props.orders[order] > 0 && props.orders[order] != undefined) {
      console.log(props.orders);
      console.log(props.details);
      totalAmount = +props.orders[order] * props.details[order];
      console.log(totalAmount);
      return (
        <li class="orderline">
          <span class="product-name">{order}</span>
          <span class="price">
            {props.orders[order] * props.details[order]}
          </span>
          <br />
          <em>{props.orders[order]}</em>
          <span> </span>Units at {props.details[order]} / Units
        </li>
      );
    }
  });
  console.log(billDetail);
  return (
    <div className="order">
      <ul class="orderlines">{billDetail}</ul>
      <hr />
      {totalAmount}
    </div>
  );
};

export default OrderSummary;
