import React, { Component } from "react";

const OrderSummary = (props) => {
  console.log(props);

  /*<button onClick={this.printReceipt}>Print Receipt</button>
  /*printReceipt() {
    window.print();
  }
  */

  return Object.keys(props.orders).map((order) => {
    if (props.orders[order] > 0) {
      console.log(props.orders);
      //console.log(props.orders);
      return (
        <div className="order">
          <ul class="orderlines">
            <li class="orderline">
              <span class="product-name">
                {props.orders[order]}
                <span> </span>
              </span>
              <span class="price">35.70 DH</span>
              <ul class="info-list">
                <li class="info">
                  <em>{props.orders[order]}</em>
                  <span> </span>Units at 5.10 DH / Units
                </li>
              </ul>
            </li>
          </ul>
          <hr />
          <div class="summary clearfix">
            <div class="line">
              <div class="entry total">
                <span class="badge">Total: </span>
                <span class="value">41.70 DH</span>
                <div class="subentry">
                  {" "}
                  Taxes: <span class="value">0.00 DH</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      );
    }
  });
};

export default OrderSummary;
