import axios from "axios";
import { GET_ERRORS, GET_CLIENTS } from "./types";

export const createClient = (client, history) => async (dispatch) => {
  try {
    const res = await axios.post("http://localhost:8080/clients", client);
    history.push("/dashboard");
  } catch (err) {
    dispatch({
      type: GET_ERRORS,
      payload: err.response.data,
    });
  }
};

export const getClients = (page, limit) => async (dispatch) => {
  const res = await axios.get(
    "http://localhost:8080/clients/all?page=0&limit=15"
  );
  dispatch({
    type: GET_CLIENTS,
    payload: res.data,
  });

  /*   dispatch({
    type: GET_CLIENTS,
    payload: [],
  }); */
};
