import React, { Component } from "react";

const OrderSummary = (props) => {
  console.log(props);
  let total = 0;

  /*<button onClick={this.printReceipt}>Print Receipt</button>
  /*printReceipt() {
    window.print();
  }
  */

  let billDetail = Object.keys(props.orders).map((order) => {
    if (props.orders[order] > 0 && props.orders[order] != undefined) {
      //console.log('[orderSummary] props.orders',props.orders);
      //console.log('[orderSummary] props.details',props.details);
      console.log("[orderSummary] props.discount", props.discount);
      total += props.orders[order] * props.details[order];
      if (props.discount != undefined) {
        return (
          <li class="orderline">
            <span class="product-name" order-name={order}>
              {order}
            </span>
            <span class="price">
              {(props.orders[order] * props.details[order] * props.discount) /
                100}
            </span>
            <br />
            <em class="quantity">{props.orders[order]}</em>
            <span> </span>Units at {props.details[order]} / Units
          </li>
        );
      } else {
        return (
          <li class="orderline">
            <span class="product-name" order-name={order}>
              {order}
            </span>
            <span class="price">
              {props.orders[order] * props.details[order]}
            </span>
            <br />
            <em class="quantity">{props.orders[order]}</em>
            <span> </span>Units at {props.details[order]} / Units
          </li>
        );
      }
    }
  });

  let totalAmount = (
    <div class="summary clearfix">
      <div class="line">
        <div class="entry total">
          <span class="badge">Total: </span>
          <span class="value">{total} Dh</span>
        </div>
      </div>
    </div>
  );

  return (
    <div className="order">
      <ul class="orderlines">{billDetail}</ul>
      <hr />
      {totalAmount}
    </div>
  );
};

export default OrderSummary;
