import React, { Component } from "react";
import PropTypes from "prop-types";
import $ from "jquery";
import { connect } from "react-redux";
import {
  getProductByCategory,
  getProducts,
} from "../ProductActions/ProductActions";
import {
  getCategories,
  createCategory,
} from "../categoryActions/CategoryActions";
import classnames from "classnames";
import logo from "./juce.png";

class AddOrders extends Component {
  componentDidMount() {
    this.props.getCategories();
    this.props.getProducts();
  }
  constructor() {
    super();

    this.state = {
      products: [],
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
    this.onClick = this.onClick.bind(this);
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }
  onClick(e) {
    console.log(e.target.getAttribute("data-categoryId"));
    //this.props.getProductByCategory(e.target.getAttribute("data-categoryId"));
    $("div.column > img").each(function () {
      if (
        $(this).data("categoryId") == e.target.getAttribute("data-categoryId")
      ) {
        $("div.column > img").data().css("display", "block");
      }
    });
    //const { products } = this.props.productsCategory;
  }

  onSubmit(e) {
    e.preventDefault();
  }
  render() {
    const { categories } = this.props.categories;
    const { products } = this.props.products;
    console.log(products);
    return (
      <div className="content-wrapper">
        <div>
          <div className="register">
            <div className="container">
              <div className="row">
                <div className="col-md-8 m-auto">
                  <p className="lead text-center">Add New Order</p>
                  <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                      <ul class="list-group">
                        {categories.map((category) => (
                          <li
                            className="list-group-item"
                            data-categoryId={category.id}
                            onClick={this.onClick}
                          >
                            {category.categoryName}
                          </li>
                        ))}
                      </ul>
                    </div>

                    <div className="row">
                      <div className="column">
                        {products.map((product) => (
                          <img
                            src={logo}
                            style={{ width: 100, margin: 10, display: "none" }}
                            classname="hover-shadow"
                            onclick="{this.onClick}"
                            data-categoryId={product.category.id}
                            alt={product.productName}
                            id={product.category.id}
                          />
                        ))}
                      </div>
                    </div>

                    <input
                      type="submit"
                      className="btn btn-info btn-block mt-4"
                    />
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  all: state,
  categories: state.category,
  products: state.product,
  productsCategory: state.productCategory,
});
export default connect(mapStateToProps, {
  getCategories,
  getProducts,
  getProductByCategory,
})(AddOrders);
