import { GET_BILLS, GET_BILL } from "../BillsActions/Types";

const initialState = {
  bills: [],
  bill: {},
};

export default function (state = initialState, action) {
  switch (action.type) {
    case GET_BILLS:
      return {
        ...state,
        bills: action.payload,
      };
    case GET_BILL:
      return {
        ...state,
        client: action.payload,
      };

    default:
      return state;
  }
}
