import React, { Component } from "react";
import PropTypes from "prop-types";
import { connect } from "react-redux";
import { createProduct } from "../ProductActions/ProductActions";
import { getCategories } from "../categoryActions/CategoryActions";
import { getIngredients } from "../IngredientActions/IngredientActions";

class AddProducts extends Component {
  componentDidMount() {
    this.props.getCategories();
    this.props.getIngredients();
  }
  constructor() {
    super();

    this.state = {
      productName: "",
      quantity: "",
      price: "",
      stock: "",
      codeBar: "",
      category: { id: "" },
      ingredients: [{ id: "" }],
      errorsProductName: "",
    };
    this.onChange = this.onChange.bind(this);
    this.onSubmit = this.onSubmit.bind(this);
  }
  onChange(e) {
    this.setState({ [e.target.name]: e.target.value });
  }

  onSubmit(e) {
    e.preventDefault();
    const { name, quantity, price, stock } = this.state;
    if (name === "") {
      this.setState({
        errorsName: " name field is Required !",
      });
    }
    if (quantity === "") {
      this.setState({
        errorsQuantity: " quantity field is Required !",
      });
    }
    if (price === "") {
      this.setState({
        errorsPrice: " price field is Required !",
      });
    }
    if (stock === "") {
      this.setState({
        errorsStock: " Stock field is Required !",
      });
    }
    const newProduct = {
      productName: this.state.productName,
      quantity: this.state.quantity,
      price: this.state.price,
      stock: this.state.stock,
      codeBar: this.state.codeBar,
      category: { id: this.state.category },
      ingredients: [{ id: this.state.ingredients }],
    };
    console.log(newProduct);
    this.props.createProduct(newProduct, this.props.history);
  }
  render() {
    const { categories } = this.props.categories;
    const { ingredients } = this.props.ingredients;
    return (
      <div className="content-wrapper">
        <div>
          <div className="register">
            <div className="container">
              <div className="row">
                <div className="col-md-8 m-auto">
                  <p className="lead text-center">Add New Product</p>
                  <form onSubmit={this.onSubmit}>
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="Product Name"
                        name="productName"
                        value={this.state.productName}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="Quantity"
                        name="quantity"
                        value={this.state.quantity}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>

                    <div className="form-group">
                      <input
                        type="int"
                        className="form-control form-control-lg"
                        placeholder="price"
                        name="price"
                        value={this.state.price}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>

                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="stock"
                        name="stock"
                        value={this.state.stock}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>
                    <div className="form-group">
                      <input
                        type="text"
                        className="form-control form-control-lg"
                        placeholder="codeBar"
                        name="codeBar"
                        value={this.state.codeBar}
                        onChange={this.onChange.bind(this)}
                      />
                    </div>
                    <div className="form-group">
                      <select
                        className="form-select"
                        onChange={this.onChange}
                        name="category"
                      >
                        <option selected>Select Category</option>
                        {categories.map((category) => (
                          <option value={category.id}>
                            {category.categoryName}
                          </option>
                        ))}
                      </select>
                    </div>
                    <div className="form-group">
                      <select
                        className="form-select"
                        onChange={this.onChange}
                        name="ingredients"
                      >
                        <option selected>Select Ingredient</option>
                        {ingredients.map((ingredient) => (
                          <option value={ingredient.id}>
                            {ingredient.name}
                          </option>
                        ))}
                      </select>
                    </div>

                    <input
                      type="submit"
                      className="btn btn-info btn-block mt-4"
                    />
                  </form>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}

AddProducts.propTypes = {
  createProduct: PropTypes.func.isRequired,
};
const mapStateToProps = (state) => ({
  errors: state.errors,
  categories: state.category,
  ingredients: state.ingredient,
});
export default connect(mapStateToProps, {
  createProduct,
  getCategories,
  getIngredients,
})(AddProducts);
